import axios from "axios";
import "./App.css";
import Form from "./components/Form";
import Results from "./components/Results";
import Title from "./components/Title";
import { useState } from "react";

function App() {
  const [city, setCity] = useState();
  const [results, setResults] = useState({
    country:"",
    cityName:"",
    temperature:"",
    conditionText:"",
    icon:""
  });
  const getWeather = (e) => {
    e.preventDefault();
    axios
      .get(
        `https://api.weatherapi.com/v1/current.json?key=dec1f80e9c2c41ff81e102253240904&q=${city}&aqi=no`
      )
      .then((res) => {setResults({country:res.data.location.country , cityName:res.data.location.name, temperature:res.data.current.temp_c , conditionText:res.data.current.condition.text , icon:res.data.current.condition.icon})});
  };
  return (
    <div className="">
      <Title cityName={city} />
      <Form setCity={setCity} getWeather={getWeather} />
      <Results results = {results} />
    </div>
  );
}

export default App;
